import {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';
import Character from './components/Characters';
import Locations from './components/Locations';

function App() {
  const[characters, setCharacters] = useState([])
  const[locations, setLocations] = useState([])
  const[episodes, setEpisodes] = useState([])
  const[hideC, setHideC]= useState(false)
  const[hideL, setHideL]= useState(false)



  const getCharacters = () => {
    axios.get("https://rickandmortyapi.com/api/character").then((response) => {
      console.log(response.data.results);
      setCharacters(response.data.results)
    })
  }
  const getLocations = () => {
    axios.get("https://rickandmortyapi.com/api/location").then((response) => {
      console.log(response.data.results);
      setLocations(response.data.results)
    })
  }


const showCharacters = () => {
  setHideC(true)
}
const hideCharacters = () => {
  setHideC(false)
}

const showLocations = () => {
  setHideL(true)
}
const hideLocations = () => {
  setHideL(false)
}






  useEffect(() => {
    getCharacters()
 }, [])
  useEffect(() => {
    getLocations()
 }, [])

  return (
    <div className="App">
      <h1>Rick and Morty's App</h1>
      <button onClick={showCharacters}>Characters</button>
      <button onClick={hideCharacters}>Hide Characters</button>
      <button onClick={showLocations}>Locations</button>
      <button onClick={hideLocations}>Hide Locations</button>
      <div class="displayC">
        {characters.map((character) => {
          return(
            <div> 
              {hideC 
              ? <Character character={character}/>
              : "" }   
            </div>
          )
        })}
      </div>
      <div class="displayL">
        {locations.map((locations) => {
          return(
            <div> 
              {hideL 
              ? <Locations locations={locations}/>
              : "" }   
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
