import {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';
import Character from './components/Characters';
import Location from './components/Locations';
import Episode from './components/Episodes';

function App() {
  const[characters, setCharacters] = useState([])
  const[locations, setLocations] = useState([])
  const[episodes, setEpisodes] = useState([])
  const[hideC, setHideC]= useState(false)
  const[hideL, setHideL]= useState(false)
  const[hideE, setHideE]= useState(false)



  const getCharacters = () => {
    axios.get("https://rickandmortyapi.com/api/character").then((response) => {
      console.log(response.data.results);
      setCharacters(response.data.results)
    })
  }
  const getLocations = () => {
    axios.get("https://rickandmortyapi.com/api/location").then((response) => {
      console.log(response.data.results);
      setLocations(response.data.results)
    })
  }
  const getEpisodes = () => {
    axios.get("https://rickandmortyapi.com/api/episode").then((response) => {
      console.log(response.data.results);
      setLocations(response.data.results)
    })
  }


const showCharacters = () => {
  setHideC(true)
  setHideL(false)
  setHideE(false)
}
const home = () => {
  setHideC(false)
  setHideL(false)
  setHideE(false)
}

const showLocations = () => {
  setHideL(true)
  setHideC(false)
  setHideE(false)
}

const showEpisodes = () => {
  setHideE(true)
  setHideC(false)
  setHideL(false)
}







  useEffect(() => {
    getCharacters()
 }, [])
  useEffect(() => {
    getLocations()
 }, [])
  useEffect(() => {
    getEpisodes()
 }, [])

  return (

    <div className="App">
    <img src="/pngegg.png" alt="" width="300"/>
      <button onClick={showCharacters}>Characters</button>
      <button onClick={showLocations}>Locations</button>
      <button onClick={showEpisodes}>Episodes</button>
      <button onClick={home}>Home</button>
      <div class="displayC">
        {characters.map((character) => {
          return(
            <div> 
              {hideC 
              ? <Character character={character}/>
              : "" }   
            </div>
          )
        })}
      </div>
      <div class="displayL">
        {locations.map((location) => {
          return(
            <div> 
              {hideL 
              ? <Location location={location}/>
              : "" }   
            </div>
          )
        })}
      </div>
      <div class="displayE">
        {episodes.map((episode) => {
          return(
            <div> 
              {hideE 
              ? <Episode episode={episode}/>
              : "" }   
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
